CREATE OR REPLACE PROCEDURE TPCDI_WH.PUBLIC.ACCOUNT_UPDT_ODS_SP(levels float)
  returns string
  language javascript
  as
  $$
  var level_counter = 1
  while (level_counter <= LEVELS)
  {
